mapper.insert:
INSERT INTO
    MEMBER
    (
      email
    , password
    , name
    , grade
    )
    VALUES
    (
      #{email}
    , #{password}
    , #{name}
    , #{grade}
    )

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE email = #{email}

SELECT password
FROM MEMBER
WHERE email = #{email}

mapper.update:
UPDATE MEMBER
   SET password = #{password}
 WHERE email = #{email}
