mapper.insert:
INSERT INTO
    MEMBER
    (
      email
    , password
    , name
    , traded
    , grade
    )
    VALUES
    (
      #{email}
    , #{password}
    , #{name}
    , #{traded}
    , #{grade}
    )

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE email = #{email}

SELECT password
FROM MEMBER
WHERE email = #{email}

mapper.update:
UPDATE MEMBER
   SET password = #{password}
 WHERE email = #{email}

mapper.select:
SELECT TRADED, GRADE
  FROM MEMBER
 WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET TRADED = #{traded}
     , GRADE = #{grade}
 WHERE EMAIL = #{email}
