mapper.select:
select email, review, grade
from REVIEW
where prd_id = #{id}

mapper.insert:
INSERT INTO
    REVIEW
    (
        prd_id
      , email
      , grade
      , review
      , delete_yn
    )
    VALUES
    (
        #{id}
      , #{email}
      , #{grade}
      , #{review}
      , false
    )
